function setDefaultSelectValue(){var e=document.querySelector("div.modal.fixed select");e&&(e.value="Default")}function addAspectRatioToImage(){document.querySelectorAll("#nova button img").forEach(e=>{e.naturalWidth;var t=e.naturalHeight,a=document.createElement("div");Object.assign(a.style,{position:"absolute",top:"50%",left:"50%",transform:"translate(-50%, -50%)",overflow:"hidden",width:t+"px",height:t+"px",border:"2px solid lime"}),e.parentElement.classList.add("relative"),e.parentElement.appendChild(a)})}document.addEventListener("DOMContentLoaded",function(){setDefaultSelectValue(),setTimeout(()=>{console.log("DOM fully loaded and parsed"),addAspectRatioToImage()},100)});